package org.tnsif.intro;

public class Datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a=127;
		//Integer
		int b=128;
		//float
		float c=12/5;
		float d=5/2.5f;
		//double
		double e=12/5;
		double f=12d/5d;
		//ASCII
		int g='a';
		int h='A';
		//Char
		char i='a';
		//string
		String j="Shin chan";
		//long
		long k=98687568976675L;
		System.out.println("byte is "+a);
		System.out.println("int is "+b);
		System.out.println("float is "+c);
		System.out.println("float is "+d);
		System.out.println("double is "+e);
		System.out.println("double is "+f);
		System.out.println("ASCII is "+g);
		System.out.println("ASCII is "+h);
		System.out.println("char is "+i);
		System.out.println("string is "+j);
		System.out.println("long is "+k);
		}

	}


